mapper.insert:
INSERT INTO classroom (
    code, name, building, floor, capacity, equipment, type, status, description, image_url, admin_id
)
VALUES (
    #{code}, #{name}, #{building}, #{floor}, #{capacity}, #{equipment}, #{type}, #{status}, #{description}, #{imageUrl}, #{adminId}
)

mapper.select:
SELECT 
    id, code, name, building, floor, capacity, equipment, type, status, description, image_url, admin_id, create_time, update_time
FROM classroom


        AND code = #{code}


        AND name = #{name}


        AND building = #{building}


        AND floor = #{floor}


        AND capacity = #{capacity}


        AND equipment = #{equipment}


        AND type = #{type}


        AND status = #{status}


        AND description LIKE CONCAT('%', #{description}, '%')


        AND image_url = #{imageUrl}


        AND admin_id = #{adminId}
